import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import feiyanChinaData from './data/china_feiyan_data.json'

//中国疫情地区数据列表
let feiyanChanalist = [];

//中国疫情新增数据列表 

let newChinalist = [];



feiyanChinaData.data.forEach((item, key)=>{

  let feiyanChinaitem =  {
    name: "",
    confirm: 0,
    cure: 0,
    dead: 0,
    exist: 0,
    new: 0,
  }  
    feiyanChinaitem.name = item.name;

    item.trend.list.forEach((item_trend, key_trend)=>{
      let curr = item_trend.data.length;
      if(item_trend.name==="确诊") feiyanChinaitem.confirm = item_trend.data[curr-1];
      if(item_trend.name==="治愈") feiyanChinaitem.cure = item_trend.data[curr-1];
      if(item_trend.name==="死亡") feiyanChinaitem.dead = item_trend.data[curr-1];
      if(item_trend.name==="新增确诊"){
        feiyanChinaitem.new = item_trend.data[curr-1];

        item_trend.data.forEach((item_data, key_data)=>{

          let newChinaitem ={
            value:0,
            date:"",
          }

          if(newChinalist[key_data] === undefined) {
              newChinaitem.value = item_data;
          }
          else {
            newChinaitem.value = newChinalist[key_data].value + item_data;
          }
          newChinalist[key_data] = newChinaitem;
        });
      }
    });

    feiyanChinaitem.exist = feiyanChinaitem.confirm - feiyanChinaitem.dead - feiyanChinaitem.cure;  //计算现有的

    feiyanChanalist[key] =feiyanChinaitem;

    item.trend.updateDate.forEach((element, index) => {
        console.log(element);
        newChinalist[index].date = element;
    });
});

console.log(newChinalist);
//按照现有的病例 排序
feiyanChanalist.sort((a, b)=>{
    if(a.exist<b.exist) {
      return 1;
    }
    else{
      return -1;
    }
});



ReactDOM.render(
  <React.StrictMode>
    <App feiyanChanalist={feiyanChanalist} />
  </React.StrictMode>,
  document.getElementById('root')
);
