import React,{Component} from 'react';
import './Mysearchstyle.css'
class Mysearch extends Component{
    constructor(props){
        super(props);

        this.state={
            value: "",
            result:{
                flag:0,
                new:0,
                confirm:0,
                exist:0,
                cure:0,
                dead:0,
            }
        }
    }

    render(){

        let element=null;
        if(this.state.result.flag === 2) {
            element=(
                <div>输入错误，不是省份。请输入正确的省份</div>
            )
        }
        else if(this.state.result.flag === 1){
            element=(
                <div>right! ok!</div>
            )
        }
        return(
            <div className="mysearch">
                <div>
                    <input placeholder="请输入查询的省份" onKeyDown={this.searchEvent} value={this.state.value} onChange={this.changeEvent}></input>
                </div>
                <div>{element}</div>
            </div>
        )
    }


    search_result=()=>{
        let f=false;
        this.props.feiyanChanalist.foreach((item, index)=>{
            if(item.name == this.state.value){
                f=true;
                this.setState({
                    result:{
                        flag:1,
                        new:item.new,
                        confirm:item.confirm,
                        exist:item.exist,
                        cure:item.cure,
                        dead:item.dead,
                    }
                })
            }
        })

        if(f===false) {
            this.setState({
                result:{
                    flag:2
                }
            })
        }
    }
    searchEvent=(e)=>{
        console.log(e.keyCode);
        if(e.keyCode==13) {
            this.search_result();
        }
    }


    changeEvent=(e)=>{
        this.setState({
            value: e.target.value
        })
    }
}

export default Mysearch;
