import React,{Component} from 'react';
import './App.css'

import  MyTable from './component/MyTable/MyTable';
import MyMap from './component/MyMap/MyMap';
import Myheader from './component/Myheader/Myheader';
import Mysearch from './component/Mysearch/Mysearch';
import Mychart from './component/Mychart/Mychart';

class  App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      barstyle :{
        left: "193px"
      }
    }
  }



  render() {
    return (
        <div className='main'>
            <div className="myApp">
            <div className="header">
                <h3>新型冠状病毒肺炎</h3>
                <h1>疫情实时大数据报告</h1>
            </div>
              <div className="myTable">
                <div className="nav">
                  <div className="navitem navactive" onClick={()=>this.navEvent(0)}>国内疫情</div>
                  <div className="navitem" onClick={()=>this.navEvent(1)}>国外疫情</div>
                  <div className="bar" style={this.state.barstyle}></div>
                </div>
                <div>
                  <Myheader feiyanChanalist={this.props.feiyanChanalist} feiyanChina_yesterdead={this.props.feiyanChina_yesterdead} feiyanChina_yestercure={this.props.feiyanChina_yestercure}/>
                  <Mysearch feiyanChanalist={this.props.feiyanChanalist}/>
                  <MyMap feiyanChanalist={this.props.feiyanChanalist} />
                  <Mychart newChinalist={this.props.newChinalist} />
                  <MyTable feiyanChanalist={this.props.feiyanChanalist} />
                </div>

            </div>
            </div>
        </div>
    );
  }

  navEvent=(id)=> {
      this.setState ({
        barstyle:{
          left: (id*480+193)+'px'
        }
      });
  }

}

export default App;
