import React,{Component} from 'react';
import './Mychartstyle.css'

class Mychart extends Component{

    constructor(props){
        super(props);
    }

    
    componentDidMount() {

        let timeData = [];
        let Data = [];

        this.props.newChinalist.forEach((element, index)=> {
            console.log(element);
            timeData.push(element.date);
            Data.push(element.value);
        });
        
        console.log(timeData);
    
        let option = {
            title: {
                text: '全国 总新增确诊 趋势',
                subtext: 'mrquery 制作',
                left: 'center'
            },
            tooltip: {
                trigger: 'axis',
                axisPointer: {
                    animation: false
                }
            },
            grid: {
                left: 50,
                right: 50,
                height: '75%'
            },
            axisPointer: {
                link: {xAxisIndex: 'all'}
            },

            xAxis:  {
                    type: 'category',
                    boundaryGap: false,
                    axisLine: {onZero: true},
                    data: ['2009/6/12', '2009/6/13','2009/6/14','2009/6/15','2009/6/16']
            },
            yAxis: {
                    name: '单位/例',
                    type: 'value',
                    max: 700
            },
            series: {
                    name: '总新增确诊',
                    type: 'line',
                    symbolSize: 4,
                    hoverAnimation: false,
                    data: [0.44,0.44,0.45,0.45,0.45]
             },
        };
        let myChart = window.echarts.init(document.getElementById('mychart'));
        myChart.setOption(option);
    }
    render() {

        return(
            <div className="mychart">
                <div>my chart</div>
                <div id="mychart" className="elementchart"></div>
            </div>
        )
    }
}

export default Mychart
