import React,{Component}  from "react"
import './mymapstyle.css'
class MyMap extends Component {
    constructor (props) {
        super(props);
    }

    componentDidMount( ){
        
        let dataList=[];
        console.log(this.props.feiyanChanalist);

        this.props.feiyanChanalist.map((item, index)=>{
            let dataitem= {
                name:"",
                value: 0
            }
            dataitem.name = item.name;
            dataitem.value = item.confirm;
            dataList[index] = dataitem;
        });
        
        let option = {
            tooltip: {
                    formatter:function(params,ticket, callback){
                        return params.name+'<br />'+params.seriesName+'：'+params.value
                    }
                },
            visualMap: {
                min: 0,
                max: 10000,
                left: 'left',
                top: 'bottom',
                text: ['10000以上','0'],
                inRange: {
                    color: ['#FFFFFF', '#B70001']
                },
                show:true
            },
            geo: {
                map: 'china',
                roam: false,
                zoom:1.23,
                label: {
                    normal: {
                        show: true,
                        fontSize:'10',
                        color: 'rgba(0,0,0,0.7)'
                    }
                },
                itemStyle: {
                    normal:{
                        borderColor: 'rgba(0, 0, 0, 0.2)'
                    },
                    emphasis:{
                        areaColor: '#C8FFFD',
                        shadowOffsetX: 0,
                        shadowOffsetY: 0,
                        shadowBlur: 20,
                        borderWidth: 0,
                        shadowColor: 'rgba(0, 0, 0, 0.5)'
                    }
                }
            },
            series : [
                {
                    name: '确诊',
                    type: 'map',
                    geoIndex: 0,
                    data:dataList
                }
            ]
        };
        let myChart = window.echarts.init(document.getElementById('mymap'));
        myChart.setOption(option);
    }
    render() {
        return (
            <div>
                <div className="maptitle">
                    累 计 确 诊
                </div>
                <div id="mymap" className="mymap">
                </div>
            </div>
            
        )
    }
}

export default MyMap;
